#далее функции для обработки капчи, не используется
def getcaptch(msg):
    x = list(captchas.items())
    try:
        out = x[0][1]
        return "Введи капчу по ссылке: {}".format(out)
    except:
        return "Капч нет"
    
def incaptch(msg):
    captcha = list(captchas.items())[0][0]
    captcha.try_again(msg.strip())
    captchas.pop(captcha)
    return "Капча введена"
commands = {#команды ыыыыыыы
    "!жестко":hardfunc,
    "2.1":hardfunc,
    "!инцест":incfunc,
    "2.2":incfunc,
    "!монстр":monfunc,
    "2.3":monfunc,
    "!рандом":randfunc,
    "2.4":randfunc,
    "!пикча":picfunc,
    "1":picfunc,
    }
commands_text = {
    "!команды":helpfunc,
    }
def write_msg(user_id, msg, attach):
    return vk.method("messages.send", {"peer_id":user_id, "message":msg, "attachment":attach})
def get_input(msg, userid):#отправка сообщения
    command = msg[0]
    command = command.lower()
    if command in commands:
        comfunc = commands[command]
        write_msg(userid, '', comfunc(msg[1:]))#отправка userid - юзер, ""-сообщение, comfunc(msg[1:])-вложение
    elif command in commands_text:
        comfunc = commands_text[command]
        write_msg(userid, comfunc(msg[1:]), '')#отправка текста
    else:
        write_msg(userid, "напиши !команды", "")

while True: #обработка сообщений
    try:
        response = vk.method('messages.get', values)
        if response['items']:
            values['last_message_id'] = response['items'][0]['id']
            msg = response['items'][0]['body'].split()
            usrid = response['items'][0]['user_id']
            get_input(msg, usrid)
    except vk_api.exceptions.ApiHttpError:
        continue
